link.script:
$(document).ready(function(){
  $('.flip-slider').slick({
    infinite: true,
    slidesToShow: 2,
    slidesToScroll: 1,
    arrows: false,  
    dots: true,  
    autoplay: false,
    responsive: [
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 2
        }
      }
    ]
  });

  // Flip no clique para mobile
  $('.flip-card-inner').on('click', function() {
    if ($(window).width() < 769) {
      $(this).toggleClass('flip-active');
    }
  });

  // IntersectionObserver para flipar o primeiro card ao aparecer na tela
  const sliderElement = document.querySelector('.flip-slider');
  if (sliderElement) {
    const observer = new IntersectionObserver((entries, observer) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          // Quando aparecer na viewport, flipa o primeiro card
          const firstCard = sliderElement.querySelector('.flip-card-inner');
          if (firstCard) {
            firstCard.classList.add('flip-active');
          }
          // Para de observar depois que já flipou
          observer.unobserve(sliderElement);
        }
      });
    }, {
      threshold: 0.5 // Executa quando 50% da div estiver visível
    });

    observer.observe(sliderElement);
  }
});

document.querySelectorAll('.youtube-player').forEach(player => {
    player.addEventListener('click', function () {
        const videoId = player.getAttribute('data-id');
        const iframe = document.createElement('iframe');
        iframe.setAttribute('src', `https://www.youtube.com/embed/${videoId}?autoplay=1&mute=1`);
        iframe.setAttribute('frameborder', '0');
        iframe.setAttribute('allow', 'accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture');
        iframe.setAttribute('allowfullscreen', 'true');
        iframe.style.width = '100%';
        iframe.style.height = '415px';

        player.innerHTML = ''; // Remove a miniatura e botão de play
        player.appendChild(iframe); // Adiciona o iframe com autoplay
    });
});

document.addEventListener("DOMContentLoaded", function () {
  const observer = new IntersectionObserver(
    function (entries, observer) {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          const flipCards = entry.target.querySelectorAll('.flip-card-inner.automatico');
          flipCards.forEach(card => card.classList.add('flip-active'));
          // Para que isso aconteça apenas uma vez, você pode remover o observer:
          observer.unobserve(entry.target);
        }
      });
    },
    { threshold: 0.3 } // ajusta conforme a visibilidade desejada
  );

  const flipSlider = document.querySelector('.flip-slider');
  if (flipSlider) {
    observer.observe(flipSlider);
  }
});
